// Units

namespace MathToolkit
{
	namespace UnitConversion
	{
		namespace AngleCasting
		{
			namespace Unit
			{
				struct Radian
				{
					static constexpr double getUnitAngleInSI() { return (1.0); }
				};

				struct Degree
				{
					static constexpr double getUnitAngleInSI() { return (MathToolkit::Generic::MathConstants::getPi() / 180.0); }
				};

			}
		};
	}
}

