// Methods

namespace MathToolkit
{
	namespace UnitConversion
	{
		namespace AngleCasting
		{
			template <typename from, typename to>
			inline constexpr double cast(const double value)
			{
				return ((value * from::getUnitAngleInSI()) / to::getUnitAngleInSI());
			}

			template <typename from, typename to>
			inline constexpr double getScalingMultiplier()
			{
				return (from::getUnitAngleInSI() / to::getUnitAngleInSI());
			}
		}
	}
}

