// Units

namespace MathToolkit
{
	namespace UnitConversion
	{
		namespace LengthCasting
		{
			namespace Unit
			{
				struct AtomicUnit
				{
					static constexpr double getUnitLengthInSI() { return ((0.0529177210903 * static_cast<double>(std::nano::num)) / static_cast<double>(std::nano::den)); }
				};

				struct Angstrom
				{
					static constexpr double getUnitLengthInSI() { return ((0.1 * static_cast<double>(std::nano::num)) / static_cast<double>(std::nano::den)); }
				};

				template <typename Magnitude = std::ratio<1>>
				struct Metre
				{
					static constexpr double getUnitLengthInSI() { return (static_cast<double>(Magnitude::num) / static_cast<double>(Magnitude::den)); }
				};
			}
		}
	}
}

// Units
// **********************************************************************************************************************************************************************************************************************************************************************************************
// **********************************************************************************************************************************************************************************************************************************************************************************************
// **********************************************************************************************************************************************************************************************************************************************************************************************
