// Units

namespace MathToolkit
{
	namespace UnitConversion
	{
		namespace MassCasting
		{
			namespace Unit
			{
				struct AtomicUnit
				{
					static constexpr double getUnitMassInSI() { return (0.91093837015 / (static_cast<double>(std::peta::num) * static_cast<double>(std::peta::num))); }
				};

				template <typename Magnitude = std::ratio<1>>
				struct Gram
				{
					static constexpr double getUnitMassInSI() { return (static_cast<double>(Magnitude::num) / static_cast<double>(Magnitude::den)); }
				};
			}
		}
	}
}

// Units
