// Methods

void InitialStructureGenerationParameters::initialize() noexcept
{
	_generationMode = GenerationMode::random;
	_randomStructureGenerationParameters.initialize();
}

void InitialStructureGenerationParameters::initialize(const System::IO::StreamReader& streamReader)
{
	initialize();
	_randomStructureGenerationParameters.initialize(streamReader);


	std::string generationModeText;
	{
		streamReader.readParameter("Generation.Mode", generationModeText);

		if (generationModeText.empty())
			_generationMode = GenerationMode::random;
		else
			_generationMode = toGenerationMode(generationModeText);
	}
}

