// Private methods

InitialStructureGenerationParameters::GenerationMode InitialStructureGenerationParameters::toGenerationMode(const std::string& inputTexts)
{
	if (inputTexts == "RANDOM" || inputTexts == "Random" || inputTexts == "random")
		return GenerationMode::random;
	else
		throw System::ExceptionServices::ArgumentOutOfRangeException{ typeid(*this), "toGenerationMode", "\"Generation.Mode\" is invalid." };
}

