// Private methods

bool MpiPolicy::isMpiDirectoryName(const std::string& directoryName)
{
	std::regex pat("MPI[\\_]{1}[\\d]+");
	std::smatch sm;

	if (std::regex_match(directoryName, sm, pat))
		return true;
	else
		return false;
}

