// Constructors

SiteSymmetrySymbol::SiteSymmetrySymbol() noexcept
	: _symbol{}
{
}

SiteSymmetrySymbol::SiteSymmetrySymbol(const std::string& symbol)
	: _symbol{ symbol }
{
	if (!(isValidSymbol(_symbol)))
		throw System::ExceptionServices::ArgumentOutOfRangeException{ typeid(*this), "constructor", "Site symmetry symbol is invalid." };
}

